import './App.css'
import React, { useState } from 'react';

function Login(props) {
  const [login_ren,setRenderer] = useState(props.type==='login' ? true : false);

  var validateLogin = ()=>{

  }
  var newUserName;
  var newEmailId;
  var newPassword;
  var newConfirmPassword;
  const setUserName = (event) =>{
    newUserName = event.target.value;
  }
  const setEmailId = (event) =>{
    newEmailId = event.target.value;
  }
  const setNewPassword = (event) =>{
    newPassword = event.target.value;
  }
  const setNewConfirmPassword = (event) =>{
    newConfirmPassword = event.target.value;
  }
  var validateSignup = ()=>{
      console.log("@@INSIDE VALIDATE SIGN UP")
      console.log(newUserName);
      console.log(newEmailId);
      console.log(newPassword);
      console.log(newConfirmPassword);
  }
  
  return (
    <div class="center">
      <button class="header_button_login" onClick={()=>setRenderer(true)}>LOGIN</button>
      <button class="header_button_signup" onClick={()=>setRenderer(false)}>SIGNUP</button>
      {login_ren && (
      <div>
      <p>User Name</p>
      <input
        type="text"
        placeholder="username"
      ></input>
      <p>Password</p>
      <input type="password" placeholder="password"></input>
      <br />
      <br />
      <button type="submit">Login</button>
      </div>)} 
      {!login_ren && (
      <div>
      <p>User Name</p>
      <input type="text" placeholder="username" value={newUserName} onChange={setUserName}></input>
      <p>Email Id</p>
      <input type="Text" placeholder="Email Id" value={newEmailId} onChange={setEmailId}></input>
      <p>Password</p>
      <input type="password" placeholder="password" value={newPassword} onChange={setNewPassword}></input>
      <p>Confirm Password</p>
      <input type="password" placeholder="password" value={newConfirmPassword} onChange={setNewConfirmPassword}></input>
      <br />
      <br />
      <button type="submit" onClick={validateSignup}>Signup</button>
      </div>)} 
    </div>
  );
}

export default Login;
