import React, { useEffect, useCallback, useReducer } from "react";

const FULL_DASH_ARRAY = 283;
const WARNING_THRESHOLD = 10;
const ALERT_THRESHOLD = 5;

const COLOR_CODES = {
  info: {
    color: "green"
  },
  warning: {
    color: "orange",
    threshold: WARNING_THRESHOLD
  },
  alert: {
    color: "red",
    threshold: ALERT_THRESHOLD
  }
};

const TIME_LIMIT = 20;

const timerReducer = (state, action) => {
  switch (action.type) {
    case "TIME_TICK":
      return {
        ...state,
        timePassed: state.timePassed + 1,
        timeLeft: state.timeLeft - 1
      };
    case "SET_TIMER_INTERVAL":
      return {
        ...state,
        timerInterval: action.payload
      };
    default:
      return state;
  }
};

const Timer = () => {
  const [{ timePassed, timeLeft, timerInterval, remainingPathColor }, dispatch] = useReducer(timerReducer, {
    timePassed: 0,
    timeLeft: TIME_LIMIT,
    timerInterval: null,
    remainingPathColor: COLOR_CODES.info.color
  });

  const onTimesUp = useCallback(() => {
    clearInterval(timerInterval);
  }, [timerInterval]);

  const setCircleDasharray = useCallback(() => {
    const circleDasharray = `${(calculateTimeFraction() * FULL_DASH_ARRAY).toFixed(0)} 283`;
    document.getElementById("base-timer-path-remaining").setAttribute("stroke-dasharray", circleDasharray);
  }, [timeLeft]);

  const startTimer = useCallback(() => {
    const interval = setInterval(() => {
      dispatch({ type: "TIME_TICK" });
    }, 1000);
    dispatch({ type: "SET_TIMER_INTERVAL", payload: interval });
  }, []);

  useEffect(() => {
    startTimer();
    return () => clearInterval(timerInterval);
  }, [startTimer, timerInterval]);

  useEffect(() => {
    if (timeLeft === 0) {
      onTimesUp();
    }
  }, [timeLeft, onTimesUp]);

  useEffect(() => {
    setCircleDasharray();
  }, [setCircleDasharray]);

  const formatTime = useCallback((time) => {
    const minutes = Math.floor(time / 60);
    let seconds = time % 60;

    if (seconds < 10) {
      seconds = `0${seconds}`;
    }

    return `${minutes}:${seconds}`;
  }, []);

  const calculateTimeFraction = useCallback(() => {
    const rawTimeFraction = timeLeft / TIME_LIMIT;
    return rawTimeFraction - (1 / TIME_LIMIT) * (1 - rawTimeFraction);
  }, [timeLeft]);

  return (
    <div className="base-timer">
      <svg className="base-timer__svg" viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
        <g className="base-timer__circle">
          <circle className="base-timer__path-elapsed" cx="50" cy="50" r="45"></circle>
          <path
            id="base-timer-path-remaining"
            strokeDasharray="283"
            className={`base-timer__path-remaining ${remainingPathColor}`}
            d="M 50, 50 m -45, 0 a 45,45 0 1,0 90,0 a 45,45 0 1,0 -90,0"
          ></path>
        </g>
      </svg>
      <span id="base-timer-label" className="base-timer__label">{formatTime(timeLeft)}</span>
    </div>
  );
};

export default Timer;
