import './App.css'
import React, { useState } from 'react';

function Login(props) {
  var login_ren,signup_ren;
  if(props.type==="signup"){
    login_ren = false;
    signup_ren = true;
  }else{
    login_ren = true;
    signup_ren = false;
  }
  
  return (
    <div class="center">
      <button class="header_button_login">LOGIN</button>
      <button class="header_button_signup" onClick={Login("signup")}>SIGNUP</button>
      {login_ren && (
      <div>
      <p>User Name</p>
      <input
        type="text"
        placeholder="username"
      ></input>
      <p>Password</p>
      <input type="password" placeholder="password"></input>
      <br />
      <br />
      <button type="submit">Login</button>
      </div>)} 
    </div>
  );
}

export default Login;
