import { useState, useEffect, useRef } from "react";
import { useLocation } from 'react-router-dom';
import './authcode.css';
import './authcodeidentify.css'

const TIME_LIMIT = 20;

// Initially, no time has passed, but this will count up
// and subtract from the TIME_LIMIT
let timePassed = 0;
let timeLeft = TIME_LIMIT;

let timerInterval = null;

function startTimer() {
    timerInterval = setInterval(() => {

      // The amount of time passed increments by one
      timePassed = timePassed += 1;
      timeLeft = TIME_LIMIT - timePassed;

      // The time left label is updated
      document.getElementById("base-timer-label").innerHTML = formatTimeLeft(timeLeft);
    }, 1000);
  }

function formatTimeLeft(time) {
    // The largest round integer less than or equal to the result of time divided being by 60.
    const minutes = Math.floor(time / 60);

    // Seconds are the remainder of the time divided by 60 (modulus operator)
    let seconds = time % 60;

    // If the value of seconds is less than 10, then display seconds with a leading zero
    if (seconds < 10) {
      seconds = `0${seconds}`;
    }

    // The output in MM:SS format
    return `${minutes}:${seconds}`;
  }

function AuthCodeIdentifier() {

    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const uid = params.get('uid');

    const [authcode, setAuthCode] = useState("");
    const [userid, setuserid] = useState("");
    const apiCalled = useRef(false);
    useEffect(() => {
        console.log("user id" + uid);
        setuserid(uid);
        if (userid && !apiCalled.current) {
            fetch("http://localhost:8080/webapi/auth/getauthcode", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json",
                },
                body: JSON.stringify({
                    userid: userid
                }),
            }).then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            }).then((data) => {
                const respData = JSON.parse(JSON.stringify(data));
                setAuthCode(respData.authcode);
                startTimer();
            }).catch((e) => {
                console.log("Error in API", e);
            })
            apiCalled.current = true;
        }

    }, [uid, userid]);
    console.log("inside identifies " + authcode);
    return (
        <div>
            <div class="auth_container">
                <h1 style={{ color: "white" }}>{authcode}</h1>
                <div class="base-timer">
                    <svg class="base-timer__svg" viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
                        <g class="base-timer__circle">
                            <circle class="base-timer__path-elapsed" cx="50" cy="50" r="45" />
                        </g>
                    </svg>
                    <span>
                    ${formatTimeLeft(timeLeft)}
                    </span>
                </div>
            </div>
        </div>
    )
}

export default AuthCodeIdentifier;
