import './App.css'
import React, { useState } from 'react';

function Login(props) {
  const [activeForm, setActiveForm] = useState(props.type || 'login'); // Default to login

  const handleFormSwitch = (formType) => setActiveForm(formType);

  return (
    <div className="center">
      <button
        className="header_button_login"
        onClick={() => handleFormSwitch('login')}
        disabled={activeForm === 'login'}
      >
        LOGIN
      </button>
      <button
        className="header_button_signup"
        onClick={() => handleFormSwitch('signup')}
        disabled={activeForm === 'signup'}
      >
        SIGNUP
      </button>
      {activeForm === 'login' && (
        <div>
          {/* Login form elements here */}
          <p>User Name</p>
          <input type="text" placeholder="username" />
          <p>Password</p>
          <input type="password" placeholder="password" />
          <br />
          <br />
          <button type="submit">Login</button>
        </div>
      )}
      {activeForm === 'signup' && (
        <div>
          {/* Signup form elements here */}
          <p>Email Address</p>
          <input type="email" placeholder="email" />
          {/* Add other signup form fields */}
          <br />
          <br />
          <button type="submit">Signup</button>
        </div>
      )}
    </div>
  );
}

export default Login;
