import React, { useState, useEffect } from 'react';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';

const Timer = () => {
  const [key, setKey] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setKey(prevKey => prevKey + 1); // Increment key to force remount
    }, 120000); // Restart every 2 minutes (120000 milliseconds)

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="timer-wrapper">
        <svg>
  <defs>
    <linearGradient id="gradient-color" x1="1" y1="0" x2="0" y2="0">
      <stop offset="25%" stopColor="#074173" />
      <stop offset="55%" stopColor="#1679AB" />
      <stop offset="75%" stopColor="#5DEBD7" />
      <stop offset="95%" stopColor="#C5FF95" />
    </linearGradient>
  </defs>
</svg>
      <CountdownCircleTimer
        key={key} // Change key to force remount and restart timer
        isPlaying
        duration={120}
        colors="url(#gradient-color)"
        size={100}
        strokeWidth={6}
        onComplete={() => {
            // do your stuff here
            return { shouldRepeat: true } // repeat animation in 1.5 seconds
          }}
      >
        {({ remainingTime }) => (
          <svg>
            <text
              x="50%"
              y="50%"
              fontSize="20px"
              fontWeight="bold"
              fontFamily="Arial, sans-serif"
              fill="url(#gradient-color)" // Apply gradient to text
              textAnchor="middle"
              dominantBaseline="middle"
            >
              {remainingTime}
            </text>
          </svg>
        )}
      </CountdownCircleTimer>
    </div>
  );
};

export default Timer;
